// web/src/pages/Home.js
import React, { useState, useEffect } from 'react';
import { Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, Title, Tooltip, Legend, ArcElement } from 'chart.js';

ChartJS.register(Title, Tooltip, Legend, ArcElement);

const Home = () => {
    const [stats, setStats] = useState({ cpuUsage: [], memoryUsage: '0' });

    useEffect(() => {
        const fetchSystemStats = async () => {
            try {
                const response = await fetch('/api/system/stats');
                const data = await response.json();
                setStats(data);
            } catch (error) {
                console.error('Error fetching system stats:', error);
            }
        };

        fetchSystemStats();
        const interval = setInterval(fetchSystemStats, 60000);

        return () => clearInterval(interval);
    }, []);

    const cpuUsageData = {
        labels: ['1 minuto', '5 minutos', '15 minutos'],
        datasets: [
            {
                label: 'Carga de la CPU',
                data: stats.cpuUsage.map(u => u.toFixed(2)),
                backgroundColor: ['rgba(75, 192, 192, 0.2)', 'rgba(153, 102, 255, 0.2)', 'rgba(255, 159, 64, 0.2)'],
                borderColor: ['rgba(75, 192, 192, 1)', 'rgba(153, 102, 255, 1)', 'rgba(255, 159, 64, 1)'],
                borderWidth: 1,
            },
        ],
    };

    const memoryUsageData = {
        labels: ['Uso de RAM'],
        datasets: [
            {
                label: 'Uso de RAM',
                data: [stats.memoryUsage],
                backgroundColor: ['rgba(255, 99, 132, 0.2)'],
                borderColor: ['rgba(255, 99, 132, 1)'],
                borderWidth: 1,
            },
        ],
    };

    return (
        <div>
            <h1>Bienvenido al Gestor de Servidores</h1>
            <p>Administra tus servidores de juegos desde una interfaz web.</p>
            <p>Selecciona un juego en el menú para comenzar.</p>

            <div>
                <h2>Estadísticas del Sistema</h2>
                <div>
                    <h3>Carga de la CPU</h3>
                    <Doughnut data={cpuUsageData} />
                </div>
                <div>
                    <h3>Uso de RAM</h3>
                    <Doughnut data={memoryUsageData} />
                </div>
            </div>
        </div>
    );
};

export default Home;
