// web/src/pages/Home.js
import React, { useState, useEffect } from 'react';

const Home = () => {
    const [stats, setStats] = useState({ cpuUsage: [], memoryUsage: '0' });

    useEffect(() => {
        // Función para obtener las estadísticas del sistema
        const fetchSystemStats = async () => {
            try {
                const response = await fetch('/api/system/stats');
                const data = await response.json();
                setStats(data);
            } catch (error) {
                console.error('Error fetching system stats:', error);
            }
        };

        fetchSystemStats();
        // Actualizar cada 60 segundos
        const interval = setInterval(fetchSystemStats, 60000);

        return () => clearInterval(interval); // Limpiar intervalo al desmontar
    }, []);

    return (
        <div>
            <h1>Bienvenido al Gestor de Servidores</h1>
            <p>Administra tus servidores de juegos desde una interfaz web.</p>
            <p>Selecciona un juego en el menú para comenzar.</p>

            <div>
                <h2>Estadísticas del Sistema</h2>
                <p><strong>Carga de la CPU (promedio 1, 5 y 15 minutos):</strong> {stats.cpuUsage.join(', ')}</p>
                <p><strong>Uso de RAM:</strong> {stats.memoryUsage}%</p>
            </div>
        </div>
    );
};

export default Home;
